INSERT INTO faqs (question, answer) VALUES
('.البحث السريع', 'يمكنك البحث عن عقار معين باستخدام رقم العقار في شريط البحث الرئيسي'),
('.الإحصائيات والتقارير', 'تعرض الصفحة الرئيسية ملخصات سريعة مثل مراقب السوق وأفضل 10 وسطاء عقاريين'),
('.الأخبار والتنبيهات', 'يمكنك قراءة آخر الأخبار، ويوجد زر تابع قراءة الخبر أسفل كل خبر'),
('.واجهة البحث عن عقار', 'من تبويب العقارات يمكن اختيار كل العقارات أو عقاراتي ويمكن البحث حسب رقم العقار في خانة البحث أعلى الجدول يساراً أو في الشريط العلوي يساراً ويمكن الفلترة من زر فلتر حسب المدينة أو الحي أو أو السعر الأدنى أو السعر الأعلى أو كلهم'),
('.أزرار الوصول السريع', 'روابط أو أزرار بالشريط السفلي أو الجانبي أو الأعلى، تسمح بالوصول السريع إلى صفحات معينة'),
('.آخر نشاط للعملاء', 'يعرض اسم العميل ووقت دخوله للموقع'),
('.كيف أضيف عقار', 'من تبويب إدخال في الشريط العلوي للصفحة الرئيسية اختر إما إدخال جديد أو استيراد من سجل عقارات آخر أو استيراد من عقار سابق'),
('.خطوات إدخال عقار', 'الإدخال يتكون من 4 صفحات: المعلومات العامة، الإضافية، الموقع، الصور'),
('.عرض العقارات', 'من تبويب العقارات ثم كل العقارات حيث يمكن فلترة النتائج ويمكن تعديل أو حذف أو إضافة عقار ولكن التعديل والحذف للعقارات الخاصة بك فقط'),
('.عقاراتي', 'من تبويب العقارات ثم عقاراتي حيث يمكنك رؤية و حذف و تعديل العقارات الخاصة بك فقط'),
('.كتابة عقد', 'اذهب إلى تبويب العقود في الشريط العلوي للصفحة الرئيسية اختر كتابة عقد لإدخال عقد جديد'),
('.تعديل عقد', 'اذهب إلى تبويب العقود ثم اختر تعديل عقد، يجب رفع ملف العقد المراد تعديله'),
('.العقارات المباعة', 'من تبويب انتقالات في الشريط العلوي للصفحة الرئيسية اختر بيع، فيتم عرض العقارات التي تم بيعها'),
('.العقارات المؤجرة', 'اذهب إلى تبويب انتقالات و اختر اجار لعرض العقارات المؤجرة حالياً'),
('.حركة السوق', 'اذهب إلى تبويب التقارير و اختر حركة السوق لمشاهدة عدد العقارات المباعة ومتوسط أسعارها ونوع العقار ويمكن فلترة النتائج حسب السنة والشهر واسم المنطقة'),
('.أفضل الوكلاء', 'اذهب إلى تبويب التقارير و اختر أفضل الوكلاء لرؤية أفضل 10 وسطاء عقاريين'),
('.نشاط وكيل معين', 'من تبويب التقارير ثم نشاط الوكيل فيمكن البحث عن وكيل معين من حقل البحث أعلى الجدول يساراً عن طريق كتابة اسم الوسيط أو رقم الرخصة'),
('.الوصول إلى جهات الاتصال', 'اذهب إلى تبويب قائمة الأسماء في الشريط العلوي الرئيسي ثم جهات الاتصال حيث يمكن إضافة جهة اتصال أو الفلترة حسب اسم الشخص المطلوب'),
('.إضافة جهة اتصال', 'اذهب إلى تبويب قائمة الأسماء في الشريط العلوي الرئيسي ثم جهات الاتصال حيث يمكن إضافة جهة اتصال عبر النقر على زر إضافة جهة اتصال'),
('.المعلومات المطلوبة لإضافة جهة اتصال', 'الاسم، اسم الأب، النسبة، اسم الأم، القيد، رقم الهاتف، مكان الولادة، الرقم الوطني، كلمة المرور، تاريخ الولادة، البريد الإلكتروني'),
('.الإعدادات العامة', 'يمكن تغيير قياس الخط والأبعاد من خلال شريط التوجه الجانبي عند النقر على أيقونة الاعدادات في الزاوية العليا يساراً للشريط الرئيسي'),
('.الحساب الشخصي', 'اضغط على الصورة الشخصية للوصول إلى الملف الشخصي أو لتسجيل الخروج'),
('.كيف أعدل بيانات العقار؟', 'يمكنك تعديل بيانات العقار من خلال الذهاب إلى تبويب العقارات واختيار عقاراتي ثم الضغط على أيقونة التعديل بجانب العقار المطلوب'),
('.ما معنى القيد المطلوب لاضافة جهة اتصال', 'القيد المدني أو رقم القيد في السجل المدني (أي القيد العائلي أو رقم السجل المدني المرتبط بالشخص)');
